import React from 'react';
import { Map, GeoJSON, TileLayer } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

function Covidmap({ countriesProp }){
	console.log(countriesProp, 'KIR');
	const mapStyle = {
		fillColor: 'white',
		weight: 1,
		color: 'black',
		fillOpacity: 1
	};

	const onEachCountry = (country, layer) => {
		// layer.options.fillColor =
		// 	country.properties.color;
		const name = country.properties.ADMIN;
		const confirmedText = country.properties.confirmedText;
		layer.bindPopup(`${name} ${confirmedText}`);
	};

	// console.log(countriesProp);

	return (
		<Map style={{ height: '90vh' }} zoom={10} center={[ 41.8781, -87.6298 ]}>
			<TileLayer
				url='https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png'
				attribution='&copy; <a href=&quot;http://osm.org/copyright&quot;>OpenStreetMap</a> contributors'
			/>
			<GeoJSON data={countriesProp} style={mapStyle} onEachFeature={onEachCountry} />
		</Map>
	);
}
export default Covidmap;
